Actions.Action:
(block Nil
	(if gResult
		(block Nil
			(objAddItem gPlayerShip gAmmo)
			(objSetData gPlayerShip "blackMarketWeapon" (objGetData gPlayerShip "blackMarketLevel"))
			)
		)
	(scrShowScreen gScreen "Main")
	)

PaymentInFull.OnPaneInit:
(scrSetDesc gScreen 
	(cat "Lord Mikho is dining alone at a large table. \"Well, well! You've certainly caused us a bit of trouble! You owe us " (objGetData gPlayerShip "blackMarketCrimeCost") " credits for " (objGetData gPlayerShip "blackMarketCrime") "! You were wise to come here to repay your debt.\"")
	)

Events.GetGlobalDockScreen:
(switch ; This only applies to Black Market stations and ships (not (objMatches gSource Nil "sTAV +blackMarket;")) Nil
; If the ship is radioactive then refuse dock (shpIsRadioactive gPlayerShip) { screen:&dsRefuseContamination; priority:10 overrideOnly:true }
; If there is a bounty on the player, then the player ; has to repay the debt. (objGetData gPlayerShip "blackMarketCrimeCost") { screen:&dsBlackMarketPayDebt; priority:8 overrideOnly:true }
; If the player does not have a black market ID, then ; refuse dock (not (objGetItems gPlayerShip "*+BlackMarketID")) { screen:&dsBlackMarketRefuseDock; priority:6 overrideOnly:true }
; See if the player is promoted (intBlackMarketPromotion) { screen:&dsBlackMarketPromotion; priority:4 overrideOnly:true }
Nil )
