Events.GetGlobalAchievements:
(append
	;	Crime record

	(if (geq (int (objGetData gPlayerShip "commonCrimeSeverity")) 2)
		{
			desc: (typTranslate gType 'textWantedFor { crimeText: (objGetData gPlayerShip 'commonCrime) })
			sectionID: 'criminalRecord
			}
		)
	)

Events.GetGlobalDockScreen:
; If the player has docked with an active commonwealth station ; with customs, then we need to check for various things ; (like radiation poisoning and smuggling). ; ; NOTE: We return a screen and a priority. The priority is used ; to decide whether other screens should be shown before or after ; these. Follow these rules: ; ; Screens that prevent docking: 10 ; Screens that imprison the player: 8 ; Screens that confiscate: 6 ; Screens that complete missions: 4
(switch ; This does not apply to stations without customs (not (objMatches gSource Nil "sTAV +commonwealthCustoms;")) Nil
; If customs is temporarily disabled, then we don't do anything. (= (objFireEvent gSource 'GetCommonwealthCustomsStatus) 'disabled) Nil
; If the player ship is radioactive, then we need to ; decontaminate. (shpIsRadioactive gPlayerShip) { screen:&dsCommonwealthDecon; priority:10 overrideOnly:true }
; If the player has sold slaves then he/she is arrested. (geq (int (objGetData gPlayerShip "commonCrimeSeverity")) 2) { screen:&dsCommonwealthImprison; priority:8 overrideOnly:true }
; If the player has slaves then free them (and (objGetItems gPlayerShip "*+Slaves;") (not (objGetData gPlayerShip "00001002_ConfiscateSlaves")) ) { screen:&dsCommonwealthFreeSlaves; priority:8 overrideOnly:true }
; If the player has a black market container, then she is imprisoned (and (not (objGetItems gPlayerShip "*I+SmugglersHold;")) (objGetItems gPlayerShip "*U+SealedContainer;") (not (objGetData gPlayerShip "00001002_ConfiscateSealedContainer")) ) (block Nil ; This side-effect is OK because it is OK if we call it multiple ; times, and it is OK if some other screen takes priority. (intCommonwealthCrime 2 "smuggling")
{ screen:&dsCommonwealthImprison; priority:8 overrideOnly:true } )
; If we have illegal items in the cargo hold then we need ; to confiscate them (and (not (objGetItems gPlayerShip "*I+SmugglersHold")) (or (objGetItems gPlayerShip "*U+Illegal; -ID") (and (objGetItems gPlayerShip "*U+Military") (not (objGetItems gPlayerShip "*+MilitaryID"))))) { screen:&dsCommonwealthConfiscate; priority:6 overrideOnly:true }
Nil )

Events.OnGlobalPlayerSoldItem:
(switch
	;	If the player sold slaves, chance they get caught

	(itmHasAttribute gItem 'slaves)
		(block (
			;	2% chance of arrest per slave sold.
			(chanceOfArrest 2)
			)
			(objIncData gPlayerShip 'slaveSales (ecoExchange aPrice aCurrency 'credit))

			(if (rollChance chanceOfArrest (itmGetCount gItem))
				(intCommonwealthCrime 2 "slave-trading")
				)
			)
	)

