Events.GetGlobalAchievements:
(block (medsDonated)

	(append
		;	Rank
		(if (gr (objGetData gPlayerShip "fleetXP") 0)
			(list
				(list
					"Commonwealth Fleet rank"
					(typTranslate &svCommonwealthFleet; 'PlayerRank)
					)
				)
			)

		;	Missions
		(rpgMissionAchievements &svCommonwealthFleet; "* +commonwealthFleet;")

		;	Meds donated
		(if (setq medsDonated (typGetGlobalData &svCommonwealthFleet; "medsDonated"))
			(list
				(list
					"Value of supplies donated to Commonwealth Fleet"
					medsDonated
					(typTranslate &unidCommonText; 'missionsAndActivities)
					)
				)
			)
		)
	)

Events.GetGlobalDockScreen:
(switch ; This only applies to Fleet ships and stations (not (objMatches gSource Nil "sTAV +commonwealthFleet;")) Nil
; If the ship is radioactive then we need to decontaminate (shpIsRadioactive gPlayerShip) { screen:&dsFleetDecon; priority:10 overrideOnly:true }
; The remaining screens are only invoked if the ship/station ; implements Fleet Law (e.g., CSC Antarctica does not). (not (objHasAttribute gSource "fleetLaw")) Nil
; If the player has committed a crime, then imprisonment (geq (int (objGetData gPlayerShip "fleetCrimeSeverity")) 2) { screen:&dsFleetImprison; priority:8 overrideOnly:true }
; If the player has committed a Commonwealth crime then she ; is kicked out of the military. (and (geq (int (objGetData gPlayerShip "commonCrimeSeverity")) 2) (leq (objGetData gPlayerShip "fleetLevel") 3)) { screen:&dsFleetDischarge; priority:8 overrideOnly:true }
; If the player does not have a military ID, then ; refuse dock (not (objGetItems gPlayerShip "*+MilitaryID")) { screen:&dsFleetRefuseDock; priority:6 overrideOnly:true }
; If the player has committed a Commonwealth crime but is a ; Fleet lieutenant or higher, then she gets a warning. (and (geq (int (objGetData gPlayerShip "commonCrimeSeverity")) 2) (not (eq (typGetGlobalData &svCommonwealthFleet; 'commonCrimeWarning) (objGetData gPlayerShip "commonCrime")))) { screen:&dsFleetCrimeWarning; priority:5 overrideOnly:true }
Nil )

