Actions.Action:
(rpgMissionAssignment {
	missionCriteria: (cat "n +commonwealthMining; =" (sysGetLevel) ";")
	noMissionTextID: 'descNoMissions
	maxPerStation: (+ 1 (modulo (objGetDestiny gSource) 3))
	intervalPerStation: (+ 5400 (* (objGetDestiny gSource) 10))
	})

