Events.OnObjDestroyed:
(switch
	(objGetData aObjDestroyed "00101002_Illegal")
		(block Nil
			(if (and gPlayerShip (= aOrderGiver gPlayerShip))
				(msnSetData gSource 'playerHelped True)
				)
			(if (leq (msnIncData gSource 'targetCount -1) 0)
				(msnSuccess gSource)
				)
			)
	)

