Events.GetParameters:
(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		particleCount: (mathScale damageHP 10 140 100 300 50)
		length: 72 ;(mathScale damageHP 10 140 60 220 50)
		width: 9
		}
	)

(block (damageHP)
	(setq damageHP (@ gData 'damageHP))

	{
		length:	(mathScale damageHP 5 64 60 220 50)
		width: (mathScale damageHP 5 64 10 20 50)
		intensity: 20
		}
	)

Language.Text:
(cat "You are docked at the remains of a pteravore lair. "
	"These fearsome silicon-based lifeforms live on refined pteracnium, extracted from unsuspecting vessels."
	)

Events.OnDestroy:
(if (and aOrderGiver
		(objCanAttack aOrderGiver)
		)
	(block Nil
		; Remember who killed us
		(objSetObjRefData gSource "destroyer" aOrderGiver)

		; Start a timer
		(sysAddObjRecurringTimerEvent 17 gSource "OnRevenge")
		)
	)

Events.GetParameters:
(block (
	(damageHP (@ gData 'damageHP))
	(weaponUNID (@ gData 'weaponUNID))
	)

	{
		radius: (if (= damageHP 0) 10 (mathScale damageHP 5 64 20 45 50))
		;lifetime: (if (= damageHP 0) 4 (mathScale damageHP 5 64 12 48 50))

		intensity: (seededRandom weaponUNID 50 70)
		distortion: (mathScale damageHP 5 64 50 0 100)
		detail: (seededRandom weaponUNID 20 25)
		}
	)

Events.OnProduceMissiles:
(if (not (objIsAbandoned gSource))
	(block (theItems)
		(setq theItems (objGetItems gSource "m +NAMILauncher;"))
		(if (or (not theItems) (ls (itmGetCount (item theItems 0)) 16))
			(block (newMissiles)
				(setq newMissiles (random 10 40))
				(objAddItem gSource (itmCreate &itXM300Missile; newMissiles))
				)
			)
		)
	)
