Events.OnAccepted:
(block (theArchive)
	;	Create the archive
	(setq theArchive
		(sysCreateStation &stMaryamsArchive; 
			(sysVectorRandom Nil (random 400 800) 150 "T")
			)
		)

	;	Remember it
	(msnSetData gSource 'targetID (objGetID theArchive))

	;	Give the player the Neurohack ROM
	(objAddItem gPlayerShip (itmCreate &itNeurohackROM; 1))
	(itmSetKnown (itmCreate &itNeurohackROM; 1))
	)

Language.Text:
(msnTranslate gSource 'textSummary {
	systemName: (sysGetName)
	})

(list
	{	textID: 'textIntro1	}
	{	textID: 'textIntro2	}
	)

StaticData.Data:
(
	{ id:'q1a	dependsOn:'q1	}
	{ id:'q1b	dependsOn:'q1	}
	{ id:'q1b1	dependsOn:'q1b	}
	{ id:'q1	}
	{ id:'q2	code: (lambda () (msnFireEvent (msnFind "Pa +unid:&msHereticMaryamsArchive;;") 'OnGiveROM))	}
	{ id:'q3	}
	{ id:'q4	}
	)

NoROM.OnPaneInit:
(scrSetDesc gScreen
	"You cannot attempt to subdue the AI without a neurohack ROM."
	)
