TranscendenceModule.Globals:
(block Nil
	(setq rpgFindIndexByID (lambda (dialogDesc id)
		(block (
			(i 0)
			(pageFound Nil)
			)

			(enumwhile dialogDesc (not pageFound) pageDesc
				(block Nil
					(if (eq (@ pageDesc 'id) id)
						(setq pageFound i)
						)

					(setq i (add i 1))
					)
				)

			pageFound
			)
		))
	)
