Default.OnPaneInit:
(block Nil
	(if (not (objGetData gPlayerShip "sistersAttitude"))
		(objSetData gPlayerShip "sistersAttitude" 0)
		)

	(if (objGetData gSource "freeSanctum")
		(scrSetDesc gScreen (objTranslate gSource 'WelcomeFreeSanctum))
		(scrSetDesc gScreen (objTranslate gSource 'Welcome))
		)
	)

Language.Text:
(block (
	(stargateObj (objGetObjByID (scrGetData gScreen 'stargateID)))
	)
	(cat
		"A connection with Domina is denied to you, but your brief attempt "
		"points the way to your destiny's end.\n\n"

		"Step through the " (objGetName stargateObj) " to the next system "
		"and you will be one step nearer to your goal."
		)
	)

Display.OnDisplayInit:
(map (typFind "p +domina;") thePower
	(block (
		(status (typFireEvent thePower 'GetInvokeStatus))
		(rank (typGetStaticData thePower 'rank))
		)
		{
		title: (typGetProperty thePower 'name)
		desc: (switch
			(= status 'tooHighLevel)
				(scrTranslate gScreen 'textNotAvailable)

			(= status 'cooldown)
				(scrTranslate gScreen 'textCooldown)

			(or (typTranslate thePower 'power.lineDesc)
				(scrTranslate gScreen 'textReadyToInvoke)
				)
			)

		powerType: thePower
		rank: rank
		rankNoun: (typTranslate &svDomina; 'nounRank { rank:rank gender:(plyGetGenome gPlayer) })
		status: status
		}
		)
	)

Default.OnPaneInit:
(block (
	(theEntry (scrGetListEntry gScreen))
	(thePower (@ theEntry 'powerType))
	(status (@ theEntry 'status))
	)
	(scrSetControlValue gScreen 'powerDesc
		(scrTranslate gScreen 'descPower {
			powerName: (typGetProperty thePower 'name)
			powerDesc: (typTranslate thePower 'power.desc)
			})
		)

	(switch
		(= status 'tooHighLevel)
			(scrSetDescTranslate gScreen 'descTooHighLevel {
				rankNeeded: (fmtNoun (@ theEntry 'rankNoun) 1 'article)
				})

		(= status 'cooldown)
			(scrSetDescTranslate gScreen 'descCooldown)

		(scrSetDescTranslate gScreen 'descInvokeHelp {
			key: (uiKeyLabel 'InvokePower)
			})
		)
	)

Actions.Action:
(block (count)
	(setq count (scrGetCounter gScreen))
	(if (gr count gMaxCount)
		(scrSetCounter gScreen gMaxCount)
		(block Nil
			(setq gItem (scrRemoveItem gScreen count))
			(scrShowPane gScreen "Donate")
			)
		)
	)

Events.GetGlobalDockScreen:
(switch ; This only applies to stations of the Sisters. (not (objMatches gSource Nil "sTAV +sistersOfDomina;")) Nil
; If the player ship is radioactive, then we need to ; decontaminate. (shpIsRadioactive gPlayerShip) { screen:&dsSistersDecon; priority:10 overrideOnly:true }
Nil )

ShipClass.Names:
Hand of Benevolence; Hand of Charity; Hand of Comfort; Hand of Compassion; 
Hand of Hope; Hand of Mercy; Hand of Refuge; Hand of Relief; Hand of Rescue; 
Hand of Salvation; Hand of Succor; Hand of Tranquility
