Default.OnPaneInit:
(block (theText)
	(setq theText (itmGetData gItem "Text"))
	(if (not theText)
		(setq theText (itmGetStaticData gItem "Text"))
		)

	(if theText
		(block Nil
			(scrSetDisplayText gScreen "text" theText)
			(scrSetDesc gScreen "The ROM contains a document.")
			)
		(block Nil
			(scrSetDisplayText gScreen "text" "")
			(scrSetDesc gScreen "The ROM is blank.")
			)
		)
	)

Actions.Action:
(if (gr gCost 0)
	(if (gr gMaxCount 1)
		(scrShowPane gScreen "SellQuantity")
		(if (eq gMaxCount 1)
			(block Nil
				(rpgSellItems 1 gCost)
				(setq gResult (add gResult gCost))
				(scrShowPane gScreen "Default")
				)
			)
		)
	)

Default.OnPaneInit:
(block (
	(languageTable (objGetData gSource 'languageTable))
	(infoText (or
		(typTranslate languageTable (objGetData gSource 'infoID))
		(objGetData gSource 'info)
		(scrTranslate gScreen 'core.stationDesc)
		))
	)
	(scrSetDesc gScreen infoText)
	)
