Actions.Action:
(block Nil
	(enum gList auton
		(for i 0 (subtract (shpGetArmorCount auton) 1)
			(objRepairArmor auton i)
			)
		)

	(plyCharge gPlayer gCost)
	(scrShowPane gScreen "Default")
	)

