Actions.Action:
(block (thisItem)
	(setq thisItem (scrGetItem gScreen))

	(if (and thisItem (geq (plyGetCredits gPlayer) gCost))
		(block Nil
			(shpInstallArmor gPlayerShip (scrGetItemListCursor gScreen) gArmorSegment)
			(plyCharge gPlayer gCost)
			(scrShowScreen gScreen "&dsRepairArmor;")
			)
		)
	)

(block (thisItem)
	(setq thisItem (scrGetItem gScreen))

	(if (and thisItem (eq gResult 0) (geq (plyGetCredits gPlayer) gCost))
		(block Nil
			(shpInstallDevice gPlayerShip (scrGetItemListCursor gScreen))
			(plyCharge gPlayer gCost)
			(scrShowScreen gScreen gPrevScreen gPrevPane)
			)
		)
	)

(block (thisItem)
	(setq thisItem (scrGetItem gScreen))


	(if (and thisItem (eq gResult 0) (>= (plyGetCredits gPlayer) gCost))
		(block Nil
			(shpInstallDevice gPlayerShip (scrGetItemListCursor gScreen))
			(plyCharge gPlayer gCost)
			(scrShowScreen gScreen gPrevScreen gPrevPane)
			)
		)

	)

(block (dsDryDock thisItem)
	(setq thisItem (scrGetItem gScreen))

	(if (and thisItem (geq (plyGetCredits gPlayer) gCost))
		(block Nil
			(shpRemoveDevice gPlayerShip (scrGetItemListCursor gScreen))
			(plyCharge gPlayer gCost)
			(scrShowScreen gScreen gPrevScreen gPrevPane)
			)
		)
	)

Default.Initialize:

(block (thisItem)
	(setq thisItem (scrGetItem gScreen))
	(if thisItem
		(block Nil
			(setq gCost (divide (itmGetInstallCost thisItem) 2))
			(scrSetDesc gScreen
				(cat "This weapon can be removed from your ship for " gCost " credits.")
				)
			)
		(scrSetDesc gScreen "You do not have any weapons that can be removed.")
		)

	; Enable/disable actions
	(scrEnableAction gScreen 0
		(and thisItem (geq (plyGetCredits gPlayer) gCost))
		)
	)

Actions.Action:
(block (dsDryDock thisItem)
	(setq thisItem (scrGetItem gScreen))

	(if (and thisItem (geq (plyGetCredits gPlayer) gCost))
		(block Nil
			(shpRemoveDevice gPlayerShip (scrGetItemListCursor gScreen))
			(plyCharge gPlayer gCost)
			(scrShowScreen gScreen gPrevScreen gPrevPane)
			)
		)
	)

(if (gr gMaxCount 1)
	(scrShowPane gScreen "RefuelQuantity")
	(if (eq gMaxCount 1)
		(block (itemsToUse)
			(setq itemsToUse (scrRemoveItem gScreen 1))
			(shpRefuelFromItem gPlayerShip itemsToUse)
			(scrShowScreen gScreen "&dsShipInterior;")
			)
		)
	)

