Events.OnCreate:
(block (gate gateList count)
	; Get the list of gates in the system
	(setq gateList (sysFindObject gStation "G"))

	; Place nav beacons between us and the stargates in this system
	(setq count 0)
	(enum gateList gate
		(block (beacon)
			(setq beacon
				(sysCreateStation &stNavBeacon;
					(sysVectorAdd 
						(objGetPos gStation)
						(sysVectorDivide 
							(sysVectorSubtract 
								(objGetPos gate) 
								(objGetPos gStation)
								)
							2
							)
						)
					)
				)

			; Name the nav beacons
			(if (ls count 5)
				(objSetName beacon
					(cat "NavBeacon " (item '('Alpha 'Beta 'Gamma 'Delta 'Epsilon) count))
					)
				)

			; Remember which NavBeacon goes with each gate
			(objSetObjRefData gate "NavBeacon" beacon)

			(setq count (add count 1))
			)
		)
	)

Events.OnObjDestroyed:
(if (eq (objGetData gStation "MissionStatus") "inprogress")
	(block (missionID)
		(setq missionID (objGetData gStation "MissionID"))
		(switch
			(or (eq missionID "mission1a") (eq missionID "mission1b"))
				(block (theShip)
					(setq theShip (objGetObjRefData gStation "ShipToDestroy"))

					; If the ship has been destroyed, then we succeeded
					(if (eq aObjDestroyed theShip)
						(objSetData gStation "MissionStatus" "success")
						)
					)

			(eq missionID "mission2a")
				(block (theShip)
					(setq theShip (objGetObjRefData gStation "Target"))

					; If the freighter was destroyed, we fail
					(if (eq aObjDestroyed theShip)
						(objSetData gStation "MissionStatus" "failure")
						)
					)
			)
		)
	)

Events.OnObjDocked:
(if (eq (objGetData gStation "MissionStatus") "inprogress")
	(block (missionID)
		(setq missionID (objGetData gStation "MissionID"))
		(switch
			(eq missionID "mission2a")
				(block (theShip)
					(setq theShip (objGetObjRefData gStation "Target"))

					; If the freighter docked, then we succeed
					(if (eq aObjDocked theShip)
						(objSetData gStation "MissionStatus" "success")
						)
					)
			)
		)
	)

Events.OnObjEnteredGate:
(if (eq (objGetData gStation "MissionStatus") "inprogress")
	(block (missionID)
		(setq missionID (objGetData gStation "MissionID"))
		(switch
			(or (eq missionID "mission1a") (eq missionID "mission1b"))
				(block (theShip)
					(setq theShip (objGetObjRefData gStation "ShipToDestroy"))

					; If the ship managed to gate out then the mission has failed
					(if (eq aObj theShip)
						(objSetData gStation "MissionStatus" "failure")
						)
					)
			)
		)
	)

